import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import ".././App.css";

import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const validationSchema = yup.object().shape({
  name: yup.string().required("Name is required"),
  age: yup.number().required("Age is required"),
  sex: yup.string().required("Sex is required"),
  email: yup.string().email("Invalid email").notRequired(),
  mobile: yup
    .string()
    .matches(/^(\+\d{1,3}[- ]?)?\d{10}$/, "Invalid Indian mobile number")
    .required("Mobile is required"),
  emergencyContact: yup
    .string()
    .matches(/^(\+\d{1,3}[- ]?)?\d{10}$/, "Invalid Indian mobile number")
    .notRequired(),
  govtIdType: yup.string().required("Govt ID Type is required"),
  govtId: yup.string().when(["govtIdType"], (govtIdType, schema) => {
    if (govtIdType === "Aadhar") {
      return schema
        .matches(/^\d{12}$/, "Invalid Aadhar number")
        .required("Aadhar number is required");
    } else if (govtIdType === "PAN") {
      return schema
        .matches(/^[A-Za-z]{5}\d{4}[A-Za-z]{1}$/, "Invalid PAN number")
        .required("PAN number is required");
    }
    return schema;
    }),
    guardian: yup.string(),
    occupation: yup.string(),
    maritalStatus: yup.string(),
    religion: yup.string(),
    bloodGroup: yup.string(),
    nationality: yup.string(),
    address: yup.string(),
    state: yup.string(),
    city: yup.string(),
    country: yup.string(),
    pincode: yup.string()
});

function UserRegistrationForm() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(validationSchema),
  });

  const onSubmit = (data) => {
    axios
      .post("http://localhost:4000/api/users", data)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
              <h4>Personal details</h4>
              <div className="personal">
                <div className="name">
                  <label>Name</label>
                  <input
                    
                    {...register("name", { required: true })}
                    type="text"
                    name="name"
                  />
                  {errors.name && (
                    <p className="invalid-feedback">{errors.name.message}</p>
                  )}
                </div>
                <div className="age">
                  <label>Age</label>
                  <input
                    
                    type="number"
                    name="age"
                    {...register("age", { required: true })}
                  />
                  {errors.age && <p>{errors.age.message}</p>}
                </div>
                <div className="sex">
                  <label>Sex</label>
                  <input
                    
                    {...register("sex", { required: true })}
                    type="text"
                    name="sex"
                  />
                  {errors.sex && <p>{errors.sex.message}</p>}
                </div>   
                
              </div>
              <div className="personal1">
                <div className="mobile">
                    <label>Mobile</label>
                    <input
                      
                      {...register("mobile", { required: true })}
                      type="text"
                      name="mobile"
                    />
                    {errors.phone && <p>{errors.phone.message}</p>}
                </div>
                <div className="govtId">
                    <label>ID Type</label>
                    <select
                      className={`form-control ${
                        errors.govtIdType ? "is-invalid" : ""
                      }`}
                      name="govtIdType"
                      {...register("govtIdType", { required: true })}
                    >
                      <option value="">Select ID Type</option>
                      <option value="Aadhar">Aadhar</option>
                      <option value="PAN">PAN</option>
                    </select>
                    {errors.govtIdType && <p>{errors.govtIdType.message}</p>}
                    
                </div>
                <div className="idType">
                    <label>Govt Issued ID</label>
                    <input
                      
                      type="text"
                      name="govtId"
                      {...register("govtId", { required: true })}
                    />
                    {errors.govtId && <p>{errors.govtId.message}</p>}
                </div>
              </div>

              {/* perosnal end */}
              <h4>Contact Details</h4>
              <div className="contact">
                <div className="guardian">
                  <label>Guardian Details</label>
                  <input
                    
                    type="text"
                    name="guardian"
                    {...register("guardian", { required: true })}
                  />
                  {errors.guardian && <p>{errors.guardian.message}</p>}

                </div>
                <div className="email">
                  <label>Email</label>
                  <input
                    
                    type="email"
                    name="email"
                    {...register("email", { required: true })}
                  />
                  {errors.email && <p>{errors.email.message}</p>}

                </div>
                <div className="emergency">
                  <label>Emergency Contact Number</label>
                  <input
                    
                    {...register("emergencyContact", { required: true })}
                    type="text"
                    name="emergencyContact"
                    
                  />
                  {errors.emergencyContact && (
                    <p>{errors.emergencyContact.message}</p>
                  )}
                </div>

              </div>
              <h4>Address Details</h4>
              <div className="addressDetails">
                <div className="address">
                  <label>Address</label>
                    <input
                      
                      {...register("address", { required: true })}
                      type="text"
                      name="address"
                    />
                    {errors.address && (
                      <p>{errors.address.message}</p>
                    )}
                </div>
                <div className="state">
                  <label>State</label>
                  <input
                    
                    {...register("state", { required: true })}
                    type="text"
                    name="state"
                  />
                  {errors.state && (
                    <p>{errors.state.message}</p>
                  )}
                </div>
                <div className="city">
                  <label>City</label>
                  <input
                    
                    {...register("city", { required: true })}
                    type="text"
                    name="city"
                  />
                  {errors.city && (
                    <p>{errors.city.message}</p>
                  )}
                </div>

              </div>
              <div className="addressDetails1">
                <div className="country">
                  <label>Country</label>
                  <input
                    
                    {...register("country", { required: true })}
                    type="text"
                    name="country"
                  />
                  {errors.country && (
                    <p>{errors.country.message}</p>
                  )}
                </div>
                <div className="pincode">
                  <label>Pincode</label>
                  <input
                    
                    {...register("pincode", { required: true })}
                    type="text"
                    name="pincode"
                  />
                  {errors.pincode && (
                    <p>{errors.pincode.message}</p>
                  )}
                </div>

              </div>
              <h4>Other Details</h4>
              <div className="otherDetails">
                <div className="occupation">
                  <label>Occupation</label>
                  <input
                    
                    {...register("occupation", { required: true })}
                    type="text"
                    name="occupation"
                  />
                  {errors.occupation && (
                    <p>{errors.occupation.message}</p>
                  )}
                </div>
                <div className="religion">
                  <label>Religion</label>
                  <input
                    
                    {...register("religion", { required: true })}
                    type="text"
                    name="religion"
                  />
                  {errors.religion && (
                    <p>{errors.religion.message}</p>
                  )}
                </div>
                <div className="maritial">
                  <label>Maritial Status</label>
                  <input
                    
                    {...register("maritalStatus", { required: true })}
                    type="text"
                    name="maritalStatus"
                  />
                  {errors.maritalStatus && (
                    <p>{errors.maritalStatus.message}</p>
                  )}
                </div>
                <div className="blood">
                    <label>Blood Group</label>
                    <select
                      name="bloodGroup"
                      {...register("bloodGroup", { required: true })}
                    >
                      <option value="">Select Group</option>
                      <option value="A+">A+</option>
                      <option value="A-">A-</option>
                      <option value="AB+">AB+</option>
                      <option value="AB-">AB-</option>
                      <option value="B+">B+</option>
                      <option value="B-">B-</option>
                      <option value="O+">O+</option>
                      <option value="O-">O-</option>
                    </select>
                    {errors.bloodGroup && <p>{errors.bloodGroup.message}</p>}
                    
                </div>

              </div>
              <div className="nationality">
                <label>Nationality</label>
                <input
                  
                  {...register("nationality", { required: true })}
                  type="text"
                  name="nationality"
                />
                {errors.nationality && (
                  <p>{errors.nationality.message}</p>
                )}

              </div>
              
           
              
            </div>
            <button className="cancel" type="submit">
                  Cancel
              </button>
              <input type="submit" />
          </form>
        
  );
}

export default UserRegistrationForm;
